										[Test]
		public void ShouldSaveThenDiscard${TableNameSingularPascalCase}Test()
		{
			Repository repository;
			I${TableNameSingularPascalCase} @${TableNameSingularCamelCase};
			bool result;

			Assert.IsNull(UnitOfWork.Current);

			repository = new Repository();

			using (AmbientUnitOfWorkScope scope = new AmbientUnitOfWorkScope(repository))
			{
				Assert.IsNotNull(UnitOfWork.Current);

				@${TableNameSingularCamelCase} = repository.CreateModel<I${TableNameSingularPascalCase}>();


								
									
										
											
												
											
											
												
											
										
									
									

				@${TableNameSingularCamelCase}.@${ColumnNamePascalCase} = ValueGenerator.GetNextValue<${ColumnCSharpClrType}>();

										
											
												
													
														
													
													
														
													
												
											
											


											
										
									
								



				// do INSERT first
				Assert.IsTrue(@${TableNameSingularCamelCase}.IsNew);

				result = repository.Save<I${TableNameSingularPascalCase}>(@${TableNameSingularCamelCase});

				Assert.IsTrue(result);



								
									
										
											
												
											
											
												
											
										
									
									

				${TableNameSingularCamelCase}.@${ColumnNamePascalCase} = ValueGenerator.GetNextValue<${ColumnCSharpClrType}>();

										
											
												
													
														
													
													
														
													
												
											
											


											
										
									
								



				// do UPDATE next
				Assert.IsFalse(@${TableNameSingularCamelCase}.IsNew);

				result = repository.Save<I${TableNameSingularPascalCase}>(@${TableNameSingularCamelCase});

				Assert.IsTrue(result);

				// do DELETE next
				Assert.IsFalse(@${TableNameSingularCamelCase}.IsNew);

				result = repository.Discard<I${TableNameSingularPascalCase}>(@${TableNameSingularCamelCase});

				Assert.IsTrue(result);

				scope.ScopeComplete();
			}

			Assert.IsNull(UnitOfWork.Current);
		}


							
						

								[Test]
		public void ShouldLoad${TableNameSingularPascalCase}Test()
		{
			Repository repository;
			I${TableNameSingularPascalCase} @${TableNameSingularCamelCase};

			Assert.IsNull(UnitOfWork.Current);

			repository = new Repository();

			@${TableNameSingularCamelCase} = repository.CreateModel<I${TableNameSingularPascalCase}>();

	
		
			
				
					
						
							
						
						
							
						
					
				
				
					
						
							
						
						
							
						
					
				
			
		
		
						@${TableNameSingularCamelCase}.@${ColumnNamePascalCase} = ValueGenerator.GetNextValue<${ColumnCSharpClrType}>();
			
				
					
						
							
						
						
							
						
					
				
				


				
			
		
	
	

			using (AmbientUnitOfWorkScope scope = new AmbientUnitOfWorkScope(repository))
			{
				Assert.IsNotNull(UnitOfWork.Current);

				${TableNameSingularCamelCase} = repository.Load<I${TableNameSingularPascalCase}>(@${TableNameSingularCamelCase});

				Assert.IsNotNull(@${TableNameSingularCamelCase});
				Assert.IsFalse(@${TableNameSingularCamelCase}.IsNew);

				scope.ScopeComplete();
			}

			Assert.IsNull(UnitOfWork.Current);
		}

		[Test]
		public void ShouldFind${TableNamePluralPascalCase}Test()
		{
			Repository repository;
			IEnumerable<I${TableNameSingularPascalCase}> @${TableNamePluralCamelCase};

			Assert.IsNull(UnitOfWork.Current);

			repository = new Repository();

			using (AmbientUnitOfWorkScope scope = new AmbientUnitOfWorkScope(repository))
			{
				Assert.IsNotNull(UnitOfWork.Current);

				@${TableNamePluralCamelCase} = repository.Find<I${TableNameSingularPascalCase}>(new ModelQuery(VoidExpression.Instance, new ISequence[] {}));

				Assert.IsNotNull(@${TableNamePluralCamelCase});
				Assert.IsNotEmpty(@${TableNamePluralCamelCase});

				scope.ScopeComplete();
			}

			Assert.IsNull(UnitOfWork.Current);
		}

						
							
								
									
										
									
									
										
									
								
							
							
								

							
						
					
				

			
		

		
