using System;
using System.Collections.Generic;

using TextMetal.Common.Core;

						
							
								
									
										
									
								
							
							
								
									
										using ${#item};

									
								
							
						
						
namespace ${ClrNamespace}.${FolderName}
{
	public partial class ${TableNameSingularPascalCase}
	{		
		#region Methods/Operators

		public void Mark()
		{
			DateTime now;

			now = DateTime.Now;

			this.CreationTimestamp = this.CreationTimestamp ?? now;
			this.ModificationTimestamp = !this.IsNew ? now : this.CreationTimestamp;
			this.CreationUserId = User.CurrentUserId ?? null;
			this.ModificationUserId = !this.IsNew ? User.CurrentUserId : this.CreationUserId;
			this.LogicalDelete = this.LogicalDelete ?? false;

			this.CompanyId = this.CompanyId ?? (int)Company.CurrentCompanyId;
		}

		public virtual Message[] Validate()
		{
			List<Message> messages;
			
			messages = new List<Message>();

			return messages.ToArray();
		}

		#endregion
	}
}
